mapper.delete:
delete
from
    save_board_tb
where
    save_board_userId = #{userId}
and menu_category_id = #{menuId}
and board_id = #{lunchId}

mapper.select:
select
    count(*) as saveBoardStatus
from
    save_board_tb
where
    save_board_userId = #{userId}
and menu_category_id = #{menuId}
and board_id = #{lunchId}
